Scripts.OnMouseDown:
if ( not self.isMoving ) then
self:StartMoving();
self.isMoving = true;
end

Scripts.OnMouseUp:
if ( self.isMoving) then
self:StopMovingOrSizing();
self.isMoving = false;
end

